@import "/node_modules/reset-css/reset.css";

$black: black;
$red: #be1d2c;
$redhero: rgba(190, 29, 44, 0.75);
$lightgrey: rgba(239, 239, 239, 1);

// main css 


body {
  font-family: "Montserrat", sans-serif;
  line-height: 1.5;
  font-size: 14px;
}

b {
  font-weight: bold;
}

p {
  font-weight: 400;
}

.container {
  width: 100%;
}

.row {
  max-width: 1200px;
  margin: auto;
  padding: 25px 25px;
}

@media (min-width: 0px) {

  .flex {
    display: grid;
    grid-template-columns: 1fr;
    grid-gap: 30px;
  }

  .column__image {
    img {
      width: 100%;

    }
  }

  .fixed {
    position: fixed !important;
    right: 25px;
  }


  /* -----HEADER----- */

  header {
    position: relative;
    height: 100vh;
    min-height: 500px;
  }

  .header-wrapper {
    background: $red;
  }



  nav {
    position: relative;
    background: black;

    .nav-wrapper {
      background: $black;
      display: flex;
      height: 70px;
      box-sizing: border-box;
      align-items: center;
      justify-content: space-between;
      padding: 0 20px;
      z-index: 2;
      max-width: 1200px;
      margin: auto;

      .logo {
        width: 80px;
      }

      .mobile-menu {
        display: flex !important;
      }

      ul {
        list-style-type: none;
        margin: 0;
        padding: 0;
        display: none;
        text-align: center;
        bottom: 0px;
        background: black;
        position: fixed;
        flex-direction: column;
        justify-content: center;
        width: 100%;
        height: 100%;
        z-index: 1;
        margin-left: -20px;
      }

      li {
        float: left;
        line-height: 15vh;
      }

      li a {
        display: block;
        color: white;
        text-align: center;
        padding: 14px 16px;
        text-decoration: none;
        font-family: Titillium Web, sans-serif;
        text-transform: uppercase;
        font-weight: bold;
      }


      li a:hover {
        color: $red;
      }
    }
  }

  /* -----HAMBURGER----- */

  #hamburger {
    width: 60px;
    height: 32px;
    display: block;
    z-index: 2;
    -webkit-transform: rotate(0deg);
    -moz-transform: rotate(0deg);
    -o-transform: rotate(0deg);
    transform: rotate(0deg);
    -webkit-transition: .5s ease-in-out;
    -moz-transition: .5s ease-in-out;
    -o-transition: .5s ease-in-out;
    transition: .5s ease-in-out;
    cursor: pointer;

    span {
      display: block;
      position: absolute;
      height: 5px;
      width: 100%;
      background: $red;
      border-radius: 9px;
      opacity: 1;
      left: 0;
      -webkit-transform: rotate(0deg);
      -moz-transform: rotate(0deg);
      -o-transform: rotate(0deg);
      transform: rotate(0deg);
      -webkit-transition: .25s ease-in-out;
      -moz-transition: .25s ease-in-out;
      -o-transition: .25s ease-in-out;
      transition: .25s ease-in-out;
    }

    span:nth-child(1) {
      top: 0px;
    }

    span:nth-child(2) {
      top: 14px;
    }

    span:nth-child(3) {
      top: 28px;
    }
  }

  #hamburger.open span:nth-child(1) {
    top: 14px;
    -webkit-transform: rotate(135deg);
    -moz-transform: rotate(135deg);
    -o-transform: rotate(135deg);
    transform: rotate(135deg);
  }

  #hamburger.open span:nth-child(2) {
    opacity: 0;
    left: -60px;
  }

  #hamburger.open span:nth-child(3) {
    top: 14px;
    -webkit-transform: rotate(-135deg);
    -moz-transform: rotate(-135deg);
    -o-transform: rotate(-135deg);
    transform: rotate(-135deg);
  }

  /* -----HERO CONTENT----- */

  .header__hero {
    height: calc(100% - 70px);
    background-color: $redhero;

    h2 {
      font-family: "Titillium Web", sans-serif;
      font-size: 3rem;
      text-transform: uppercase;
      font-weight: 800;
      line-height: 1;
    }

    h4 {
      font-size: 1.5rem;
      color: $lightgrey;
      font-weight: 300;
    }
  }

  .header__hero::before {
    content: "";
    position: absolute;
    height: 100%;
    width: 100%;
    top: 0px;
    left: 0px;
    background: url("/hero-mobile.jpg") no-repeat center center / cover;
    z-index: -1;
  }

  .header__hero__big_text {}

/* -----CONTENT----- */

  .about {
    h2 {
      font-size: 2.5rem;
      text-transform: uppercase;
    }
  }

  .underline::after {
    content: "";
    height: 4px;
    width: 154px;
    background-color: $red;
    display: flex;
    border-radius: 10px;
    margin-bottom: 25px;
  }

  .icons {
    bottom: 0px;
    position: absolute;
    margin-bottom: 25px;

    .opening,
    .phone {
      display: flex;
      align-items: center;
      margin-bottom: 25px;

      p {
        color: white;
