        text-decoration-color: white;
      }
    }


    .opening__text {}
  }

  @media (min-width: 1090px) {
    .flex {
      display: grid;
      grid-template-columns: 1fr 1fr;
      grid-gap: 30px;
    }
    #hamburger {
      display: none;
    }

    nav .nav-wrapper ul {
      display: flex;
      width: auto;
      flex-direction: row;
      height: 50px;
      position: block;
      top: 0px;
      z-index: 0;
      position: relative;
      li {
         line-height: 1.5em;
       }
    }
  }
}

